import React from 'react'
import {ChakraProvider, Box, Flex, Text, Container } from '@chakra-ui/react'

const Sidebar = () => {
    const sticky = {
        position: "-webkit-sticky", /* Safari */
        position: "sticky",
        top: "0"
    };
    return (
        <ChakraProvider>
            <Box w="350px" h="80vh" bg="tomato" style={sticky}>

            </Box>
        </ChakraProvider>
    )
}

export default Sidebar
