import React from 'react'
import {ChakraProvider, Box, Flex, Text, Container } from '@chakra-ui/react'

const Sidebar = () => {
    const sticky = {
        position: "-webkit-sticky", /* Safari */
        position: "sticky",
        top: "0"
    };
    return (
        <ChakraProvider>
            <Container w="350px" h="100vh" bg="tomato" style={sticky}>
                <Box>

                </Box>
            </Container>
        </ChakraProvider>
    )
}

export default Sidebar
